import React, { useState } from 'react';
import Service from "../../repository/Repository";
import { useNavigate } from 'react-router-dom';

const SearchAuthor = (props) => {
    const [responestate, updatedata] = React.useState()
    const history = useNavigate();
    const onFormSubmit =async (e) => {
        e.preventDefault();
        const name = e.target.name.value;
        let response=await Service.searchAuthor(name)
        console.log(response)
        updatedata(response.data)
        history('/result', {state:response});
    }
    return (
        <div className="container my-5">
            <div className="row">
                <div className="col-md-5">
                    <form onSubmit={onFormSubmit}>
                        <div className="form-group">
                            <label htmlFor="name">Book Name</label>
                            <input type="text"
                                   className="form-control"
                                   id="name"
                                   name="name"
                                   required
                                   placeholder="Enter book name"
                                // onChange={handleChange}
                            />
                        </div>
                        <button id="submit" type="submit" className="btn btn-primary mt-3">Search</button>
                    </form>
                </div>
                <div className="col-md-7 d-flex align-items-center justify-content-center">
                    <div className="rounded-circle bg-primary p-4 text-center">
                        <h4>Discover Books</h4>
                        <p>Find new books based on your interests</p>
                        <button className="btn btn-light mt-3">Browse Books</button>
                    </div>
                </div>
            </div>
        </div>
    )
}
export default SearchAuthor;
