import React, { useState } from 'react';
import Service from "../../repository/Repository";
import { useNavigate } from 'react-router-dom';

const SearchAuthor = (props) => {
    const [responestate, updatedata] = React.useState()
    const history = useNavigate();

    const onFormSubmit = async (e) => {
        e.preventDefault();
        const name = e.target.name.value;
        let response = await Service.searchAuthor(name)
        console.log(response)
        updatedata(response.data)
        history('/result', { state: response });
    }

    const handleSelectChange = async (e) => {
        const name = e.target.value;
        let response = await Service.searchAuthor(name)
        console.log(response)
        updatedata(response.data)
        history('/result', { state: response });
    }

    return (
        <div style={{
            backgroundColor: '#f2f2f2',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            height: '100vh',
        }}>
            <div style={{
                backgroundColor: 'white',
                border: '5px solid black',
                padding: '20px',
                borderRadius: '10px',
                fontSize: '24px',
                textAlign: 'center',
            }}>
                <p style={{ color: 'black', font: '20' }}>It's important to know about famous authors. Select an author from the list to see more information about them.</p>
                <form onSubmit={onFormSubmit}>
                    <div className="form-group">
                        <select
                            className="form-control"
                            id="name"
                            name="name"
                            onChange={handleSelectChange}
                            style={{ fontSize: '18px', marginBottom: '10px', width: '300px' }}
                        >
                            <option value="">Select an author</option>
                            <option value="Mark_Twain">Mark Twain</option>
                            <option value="Edgar_Allan_Poe">Edgar Allan Poe</option>
                        </select>
                    </div>
                    <br />
                    <button type="submit" className="btn btn-primary" style={{ fontSize: '18px' }}>Submit</button>
                </form>
            </div>
        </div>
    )
}

export default SearchAuthor;
