import React, { useState } from 'react';
import Service from "../../repository/Repository";
import { useNavigate } from 'react-router-dom';

const SearchAuthor = (props) => {
    const [responestate, updatedata] = React.useState()
    const history = useNavigate();
    const onFormSubmit =async (e) => {
        e.preventDefault();
        const name = e.target.name.value;
        let response=await Service.searchAuthor(name)
        console.log(response)
        updatedata(response.data)
        history('/result', {state:response});
    }
    return (
        <div style={{ backgroundColor: '#f5f5f5', padding: '20px' }}>
            <div style={{ border: '1px solid #ccc', padding: '20px', borderRadius: '10px' }}>
                <h1 style={{ fontSize: '36px', marginBottom: '20px', color: '#333' }}>Book Search</h1>
                <form onSubmit={onFormSubmit}>
                    <div className="form-group">
                        <label htmlFor="name" style={{ fontSize: '24px', color: '#333' }}>Book name</label>
                        <h3 style={{ fontSize: '18px', color: '#555' }}>Enter author name to see more data from dbpedia about him</h3>
                        <input type="text"
                               className="form-control"
                               id="name"
                               name="name"
                               required
                               placeholder="Enter book name"
                               style={{ fontSize: '24px', marginTop: '10px', padding: '10px', borderRadius: '5px', border: '1px solid #ccc' }}
                            // onChange={handleChange}
                        />
                    </div>
                    <br/>
                    <button id="submit" type="submit" className="btn btn-primary" style={{ fontSize: '24px', borderRadius: '5px', padding: '10px 20px' }}>Search</button>
                </form>
            </div>
        </div>
    )
}
export default SearchAuthor;
