import React from "react";

const Showrombooks = (props) => {
    console.log("tuj", props)
    const divStyle = {
        border: '1px solid #ccc',
        padding: '20px',
        backgroundColor: '#f8f8f8',
        color: '#333',
    };

    const h2Style = {
        fontSize: '24px',
        marginBottom: '10px',
    };

    const pStyle = {
        fontSize: '16px',
    };

    const authorsStyle = {
        marginTop: '10px',
        display: 'flex',
        flexWrap: 'wrap',
    };
    return (
        <div className="data-display" style={divStyle}>
            <h2 style={h2Style}>{props.show.label}</h2>
            <p style={pStyle}>{props.show.abstract}</p>
            <div className="authors" style={authorsStyle}>
                <h3>Authors:</h3>
                <div className="author-list">
                    {props.show.authors.map((author, index) => (
                        <div
                            key={index}
                            style={{
                                backgroundColor: index % 2 === 0 ? '#ccc' : '#bbb',
                                color: index % 2 === 0 ? '#fff' : '#333',
                                padding: '5px 10px',
                                borderRadius: '5px',
                                marginRight: '5px',
                                marginBottom: '5px',
                                flexGrow: '1',
                                flexBasis: 'calc(50% - 5px)',
                                boxSizing: 'border-box',
                            }}
                        >
                            {author}
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
}

export default Showrombooks;
