import React from "react";

const Showrombooks = (props) => {
    console.log("tuj", props)
    const divStyle = {
        border: '1px solid #ccc',
        padding: '20px',
        backgroundColor: '#f8f8f8',
        color: '#333',
    };

    const h2Style = {
        fontSize: '24px',
        marginBottom: '10px',
    };

    const pStyle = {
        fontSize: '16px',
    };

    const authorsStyle = {
        marginTop: '10px',
    };

    const ulStyle = {
        display: 'flex',
        flexWrap: 'wrap',
        listStyle: 'none',
        margin: '0',
        padding: '0',
    };

    const liStyle = {
        backgroundColor: '#ccc',
        color: '#fff',
        padding: '5px 10px',
        borderRadius: '5px',
        marginRight: '5px',
        marginBottom: '5px',
        flexGrow: '1',
        flexBasis: 'calc(50% - 5px)',
        boxSizing: 'border-box',
        textAlign: 'center',
        textTransform: 'capitalize',
    };
    return (
        <div className="data-display" style={divStyle}>
            <h2 style={h2Style}>{props.show.label}</h2>
            <p style={pStyle}>{props.show.abstract}</p>
            <div className="authors" style={authorsStyle}>
                <h3>Authors:</h3>
                <ul style={ulStyle}>
                    {props.show.authors.map((author, index) => (
                        <li key={index} style={liStyle}>{author}</li>
                    ))}
                </ul>
            </div>
        </div>
    );
}

export default Showrombooks;
