import React, { useState } from 'react';
import Service from "../../repository/Repository";
import { useNavigate } from 'react-router-dom';

const SearchAuthor = (props) => {
    const [responestate, updatedata] = React.useState()
    const history = useNavigate();
    const onFormSubmit =async (e) => {
        e.preventDefault();
        const name = e.target.name.value;
        let response=await Service.searchAuthor(name)
        console.log(response)
        updatedata(response.data)
        history('/result', {state:response});
    }
    return (
        <div className="container-fluid bg-light" style={{ height: "100vh" }}>
            <div className="row mt-5">
                <div className="col-md-5 offset-md-3">
                    <form onSubmit={onFormSubmit}>
                        <div className="form-group">
                            <label htmlFor="name" style={{ fontSize: "24px" }}>
                                Book Name
                            </label>
                            <h3 style={{ fontSize: "20px", color: "#333" }}>
                                Enter author name to see more data from DBPedia about him
                            </h3>
                            <input
                                type="text"
                                className="form-control form-control-lg"
                                id="name"
                                name="name"
                                required
                                placeholder="Enter book name"
                                style={{ fontSize: "24px" }}
                            />
                        </div>
                        <br />
                        <button
                            id="submit"
                            type="submit"
                            className="btn btn-primary btn-lg"
                            style={{ fontSize: "24px" }}>
                            Search
                        </button>
                    </form>
                </div>
            </div>
        </div>
    )
}
export default SearchAuthor;
