import React, { useState } from 'react';
import Service from "../../repository/Repository";
import { useNavigate } from 'react-router-dom';

const SearchBook = (props) => {
    const [responestate, updatedata] = React.useState()
    const history = useNavigate();
    const onFormSubmit =async (e) => {
        e.preventDefault();
        const name = e.target.name.value;
        let response=await Service.searchBook(name)
        console.log(response)
        updatedata(response.data)
        history('/resultbook', {state:response});
    }
    return (
        <div style={{ backgroundColor: 'lightpink', display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
            <form onSubmit={onFormSubmit} style={{ width: '400px', padding: '40px', borderRadius: '10px', border: '2px solid white', textAlign: 'center' }}>
                <h1 style={{ fontFamily: 'Lobster', fontSize: '36px', color: 'white', marginBottom: '40px' }}>Search for Books</h1>
                <div className="form-group">
                    <label htmlFor="name" style={{ fontFamily: 'Arial', fontSize: '20px', color: 'white', marginBottom: '10px', display: 'block' }}>Book name:</label>
                    <input type="text"
                           className="form-control"
                           id="name"
                           name="name"
                           required
                           placeholder="Enter book name"
                           style={{ borderRadius: '5px', border: 'none', padding: '10px', width: '100%', marginBottom: '20px', fontSize: '18px' }}
                    />
                </div>
                <button id="submit" type="submit" className="btn btn-primary" style={{ backgroundColor: 'white', color: 'lightpink', fontFamily: 'Arial', fontSize: '20px', padding: '10px 20px', borderRadius: '5px', border: 'none' }}>Submit</button>
            </form>
        </div>

    )
}
export default SearchBook;
