import React, { useState } from 'react';
import Service from "../../repository/Repository";
import { useNavigate } from 'react-router-dom';

const SearchAuthor = (props) => {
    const [responestate, updatedata] = React.useState()
    const history = useNavigate();
    const onFormSubmit =async (e) => {
        e.preventDefault();
        const name = e.target.name.value;
        let response=await Service.searchAuthor(name)
        console.log(response)
        updatedata(response.data)
        history('/result', {state:response});
    }
    return (
        <div className="row mt-5" style={{backgroundColor: '#f5f5f5', padding: '50px 0'}}>
            <div className="col-md-5 mx-auto">
                <form onSubmit={onFormSubmit} style={{backgroundColor: '#fff', padding: '30px', borderRadius: '10px'}}>
                    <div className="form-group">
                        <label htmlFor="name" style={{fontSize: '24px'}}>Book name</label>
                        <h3 style={{fontSize: '20px', marginBottom: '20px'}}>Enter author name to see more data from dbpedia about him</h3>
                        <input type="text"
                               className="form-control"
                               id="name"
                               name="name"
                               required
                               placeholder="Enter book name"
                               style={{fontSize: '18px', padding: '10px', borderRadius: '5px'}}
                        />
                    </div>
                    <br/>
                    <button id="submit" type="submit" className="btn btn-primary" style={{fontSize: '20px', backgroundColor: '#007bff', border: 'none', borderRadius: '5px'}}>Submit</button>
                </form>
            </div>
        </div>
    )
}
export default SearchAuthor;
