import React, { useState } from 'react';
import Service from "../../repository/Repository";
import { useNavigate } from 'react-router-dom';

const SearchAuthor = (props) => {
    const [responestate, updatedata] = React.useState()
    const history = useNavigate();
    const onFormSubmit =async (e) => {
        e.preventDefault();
        const name = e.target.name.value;
        let response=await Service.searchAuthor(name)
        console.log(response)
        updatedata(response.data)
        history('/result', {state:response});
    }
    return (
        <div style={{ backgroundColor: "#f7d7d7", height: "100vh", display: "flex", justifyContent: "center", alignItems: "center" }}>
            <div style={{ backgroundColor: "#fff", padding: "20px", borderRadius: "10px" }}>
                <h1 style={{ fontSize: "36px", marginBottom: "20px" }}>Search for Books</h1>
                <form onSubmit={onFormSubmit}>
                    <div className="form-group">
                        <label htmlFor="name">Book name</label>
                        <h3 style={{ marginBottom: "20px" }}>Enter author name to see more data from dbpedia about him</h3>
                        <input type="text"
                               className="form-control"
                               id="name"
                               name="name"
                               required
                               placeholder="Enter book name"
                            // onChange={handleChange}
                               style={{ fontSize: "24px", padding: "10px", borderRadius: "5px", width: "100%" }}
                        />
                    </div>
                    <br />
                    <button type="submit" className="btn btn-primary">Submit</button>
                </form>
            </div>
        </div>
    )
}
export default SearchAuthor;
