import React, { useState } from 'react';
import Service from "../../repository/Repository";
import { useNavigate } from 'react-router-dom';

const SearchAuthor = (props) => {
    const [responestate, updatedata] = React.useState()
    const history = useNavigate();
    const onFormSubmit =async (e) => {
        e.preventDefault();
        const name = e.target.name.value;
        let response=await Service.searchAuthor(name)
        console.log(response)
        updatedata(response.data)
        history('/result', {state:response});
    }
    return (
        <div style={{
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            height: '100vh',
            background: 'linear-gradient(135deg, #c850c0, #4158d0)',
        }}>
            <div style={{
                background: '#fff',
                padding: '50px',
                borderRadius: '10px',
                border: '5px solid #fff',
                boxShadow: '0px 0px 10px rgba(0, 0, 0, 0.3)',
                color: '#fff',
                textAlign: 'center',
                fontSize: '24px',
                fontWeight: 'bold',
            }}>
                <h3 style={{ color: '#c850c0', fontSize: '36px', margin: 0 }}>Hello</h3>
                <p style={{ margin: 0 }}>Enter author name to see more data from dbpedia about him</p>
                <form onSubmit={onFormSubmit}>
                    <div className="form-group">
                        <input type="text"
                               className="form-control"
                               id="name"
                               name="name"
                               required
                               placeholder="Enter book name"
                               style={{
                                   fontSize: '24px',
                                   padding: '10px',
                                   width: '100%',
                                   border: 'none',
                                   borderBottom: '2px solid #c850c0',
                                   margin: '20px 0',
                                   outline: 'none',
                                   boxShadow: 'none',
                               }}
                        />
                    </div>
                    <button type="submit" style={{
                        backgroundColor: '#c850c0',
                        border: 'none',
                        padding: '10px 20px',
                        color: '#fff',
                        borderRadius: '20px',
                        fontSize: '24px',
                        cursor: 'pointer',
                    }}>Submit</button>
                </form>
            </div>
        </div>
    )
}
export default SearchAuthor;
