import React, { useState } from 'react';
import Service from "../../repository/Repository";
import { useNavigate } from 'react-router-dom';

const SearchAuthor = (props) => {
    const [responestate, updatedata] = React.useState()
    const history = useNavigate();
    const onFormSubmit =async (e) => {
        e.preventDefault();
        const name = e.target.name.value;
        let response=await Service.searchAuthor(name)
        console.log(response)
        updatedata(response.data)
        history('/result', {state:response});
    }
    return (
        <div style={{ background: '#f9f9f9', minHeight: '100vh', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
            <form onSubmit={onFormSubmit} style={{ background: '#fff', padding: '50px', borderRadius: '5px', boxShadow: '0px 0px 10px rgba(0,0,0,0.1)' }}>
                <div className="form-group">
                    <label htmlFor="name" style={{ fontSize: '24px', fontWeight: 'bold' }}>Book name:</label>
                    <p style={{ fontSize: '16px' }}>Enter author name to see more data from dbpedia about them</p>
                    <input type="text" className="form-control" id="name" name="name" required placeholder="Enter book name" />
                </div>
                <br/>
                <button type="submit" className="btn btn-primary" style={{ fontSize: '20px', fontWeight: 'bold', padding: '10px 20px', background: '#007bff', border: 'none', borderRadius: '5px', color: '#fff' }}>Submit</button>
            </form>
        </div>
    )
}
export default SearchAuthor;
